mapper.select:
select a.id, a.name, u.name as owner, a.startDate, a.endDate from tbl_activity as a join tbl_user as u on a.owner=u.id
a.name like '%' #{selectName} '%'
and u.name like '%' #{selectOwner} '%'
and a.startDate > #{selectStartTime}
and a.endDate < {selectEndTime}
order by a.createTime desc limit #{pageStart},#{pageEnd}

select count(*) from tbl_activity as a join tbl_user as u on a.owner=u.id


        a.name like '%' #{selectName} '%'


        and u.name like '%' #{selectOwner} '%'


        and a.startDate > #{selectStartTime}


        and a.endDate < {selectEndTime}

mapper.delete:
delete from tbl_activity
where id in

